[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="alzain-block-1"> 
  <style>
    .alzain-block-1 table.invoice-header {
      width: 100%;
      border-collapse: collapse;
      direction: rtl;
      font-family: 'Cairo', Arial, sans-serif;
      font-size: 13px;
      font-weight: 800;
      line-height: 1.1;
    }

    .alzain-block-1 table.invoice-header th,
    .alzain-block-1 table.invoice-header td {
      padding: 4px;
      text-align: center;
      border-bottom: 2px solid #ccc;
      border-left: none;
      border-right: none;
      border-top: none;
      font-family: 'Cairo', Arial, sans-serif;
      font-weight: 800;
    }

    .alzain-block-1 table.invoice-header tr:nth-child(2) td {
      border-top: 1px solid #ccc;
    }

    .alzain-block-1 table.invoice-header tr:not(:first-child) td,
    .alzain-block-1 table.invoice-header tr:not(:first-child) th {
      background-color: #EFEFEF;
    }

    .alzain-block-1 table.invoice-header th.title {
      background-color: #00c2dd;
      color: white;
      font-size: 16px;
      font-weight: 950;
      text-align: center;
      padding: 6px;
      border: none;
      margin-bottom: 6px;
      font-family: 'Cairo', Arial, sans-serif;
      line-height: 1.1;
    }
  </style>

  <table class="invoice-header">
    <!-- عنوان الفاتورة -->
    <tr>
      <th colspan="4" class="title">{{ invoice_title or "فاتورة ضريبية" }}</th>
    </tr>
    <!-- الصف الأول -->
    <tr>
      <td>رقم الفاتورة</td>
      <td>{{ doc.name }}</td>
      <td>التاريخ</td>
      <td>{{ doc.get_formatted("posting_date") }}</td>
    </tr>
    <!-- الصف الثاني -->
    <tr>
      <td>اسم العميل</td>
      <td>{{ doc.customer }}</td>
      <td>الرقم الضريبي</td>
      <td>{{ doc.tax_id or "غير متوفر" }}</td>
    </tr>
    <!-- الصف الثالث -->
    <tr>
      <td>العنوان</td>
      <td>
        {% if doc.customer %}
          {% set address_name = frappe.db.get_value("Dynamic Link", {"link_doctype": "Customer", "link_name": doc.customer}, "parent") %}
          {% if address_name %}
            {% set address_doc = frappe.get_doc("Address", address_name) if frappe.db.exists("Address", address_name) else None %}
            {% if address_doc %}
              {{ address_doc.address_line1 or "" }}{% if address_doc.city %}, {{ address_doc.city }}{% endif %}{% if address_doc.pincode %}, {{ address_doc.pincode }}{% endif %}
            {% else %}
              -
            {% endif %}
          {% else %}
            -
          {% endif %}
        {% else %}
          -
        {% endif %}
      </td>
      <td>الرقم الموحد</td>
      <td>
        {% if doc.customer %}
          {% set unified_number = frappe.db.get_value("Additional Buyer IDs", {"parent": doc.customer, "type_code": "700"}, "value") %}
          {% if not unified_number %}
            {% set unified_number = frappe.db.get_value("Additional Buyer IDs", {"parent": doc.customer, "type_name": "700 Number"}, "value") %}
          {% endif %}
          {{ unified_number or "غير متوفر" }}
        {% else %}
          غير متوفر
        {% endif %}
      </td>
    </tr>
    <!-- الصف الرابع -->
    <tr>
      <td>رقم المعاملة</td>
      <td>{{ doc.transaction or "غير متوفر" }}</td>
      <td>رقم البوليصة</td>
      <td>{{ doc.custom_bol_no or "غير متوفر" }}</td>
    </tr>
    <!-- الصف الخامس -->
    <tr>
      <td>رقم البيان</td>
      <td>{{ doc.custom_declaration_no or "غير متوفر" }}</td>
      <td>فاتورة العميل</td>
      <td>{{ doc.custom_commercial_invoice_no or "غير متوفر" }}</td>
    </tr>
    <!-- الصف السادس -->
    <tr>
      <td>عدد الطرود</td>
      <td>{{ doc.custom_number_of_parcels or "" }}</td>
      <td>عدد الحاويات</td>
      <td>{{ doc.custom_container_qty or "" }}</td>
    </tr>
  </table>
</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% if doc.items %}
