        <td style="border-bottom: 2px solid #ccc; padding: 2px; text-align: center; font-family: 'Cairo', Arial, sans-serif; font-weight: 700; line-height: 1.1;">{{ "%.2f"|format(row.net_rate or 0) }}</td>
        <td style="border-bottom: 2px solid #ccc; padding: 2px; text-align: center; font-family: 'Cairo', Arial, sans-serif; font-weight: 700; line-height: 1.1;">{{ row.qty }}</td>
        <td style="border-bottom: 2px solid #ccc; padding: 2px; text-align: center; font-family: 'Cairo', Arial, sans-serif; font-weight: 700; line-height: 1.1;">{{ "%.2f"|format(row.net_amount or 0) }}</td>
        <td style="border-bottom: 2px solid #ccc; padding: 2px; text-align: center; font-family: 'Cairo', Arial, sans-serif; font-weight: 700; line-height: 1.1;">{{ "%.2f"|format(row.tax_rate or 0) }}%</td>
