        <td style="border-bottom: 2px solid #ccc; padding: 2px; color: #007bff; font-weight: 700; text-align: center; font-family: 'Cairo', Arial, sans-serif; line-height: 1.1;">{{ "%.2f"|format(row.total_amount or 0) }}</td>
      </tr>
      {%- endfor -%}
    </tbody>
  </table>
